package org.entirej.ejinvoice.database;import java.sql.Connection;import java.sql.SQLException;import org.entirej.framework.core.EJApplicationException;import org.entirej.framework.core.interfaces.EJFrameworkConnection;public class FrameworkConnection implements EJFrameworkConnection{    private Connection _connection;        public FrameworkConnection(Connection connection)    {        _connection = connection;    }     @Override    public void close()    {        try        {            _connection.close();        }        catch (SQLException e)        {            throw new EJApplicationException(e);        }    }     @Override    public void commit()    {        try        {            _connection.commit();        }        catch (SQLException e)        {            throw new EJApplicationException(e);        }    }     @Override    public Object getConnectionObject()    {        return _connection;    }     @Override    public void rollback()    {        try        {            _connection.rollback();        }        catch (SQLException e)        {            throw new EJApplicationException(e);        }    }}